mapper.delete:
DELETE FROM approval
WHERE approval_seq = #{approvalSeq}
	AND approval_yn != 'Y'

mapper.update:
UPDATE approval
SET approval_state = '거부',
	approval_yn = 'N',
	approval_date = now()
WHERE approval_ledger_seq = #{approvalLedgerSeq} 
	AND approval_user_id != #{approvalUserId}

UPDATE approval
SET approval_state = '승인',
	approval_yn = 'Y',
	approval_date = now()
WHERE approval_ledger_seq = #{approvalLedgerSeq} 
	AND approval_user_id = #{approvalUserId}
