mapper.select:
SELECT contract_seq ,ledger_financial_company_cd ,(SELECT code_name FROM code WHERE code_id = l.ledger_financial_company_cd) as ledgerFinancialCompanyCdName ,ledger_financial_branch_cd ,(SELECT code_name FROM code WHERE code_id = l.ledger_financial_branch_cd) as ledgerFinancialBranchCdName ,ledger_financial_product_cd ,(SELECT code_name FROM code WHERE code_id = l.ledger_financial_product_cd) as ledgerFinancialProductCdName ,ledger_customer_name ,ledger_dealer_brand_cd ,(SELECT code_name FROM code WHERE code_id = l.ledger_dealer_brand_cd) as ledgerDealerBrandCdName ,ledger_dealer_company_cd ,(SELECT code_name FROM code WHERE code_id = l.ledger_dealer_company_cd) as ledgerDealerCompanyCdName ,ledger_car_name ,ledger_car_number ,ledger_car_price ,ledger_acquisition_cost ,ledger_delivery_date ,(c.contract_add_ag_fee_sum+c.contract_ag_sliding_sum+c.contract_nomal_ag_fee_sum) as calculateFeeSum FROM contract c LEFT JOIN ledger l ON c.contract_ledger_seq=l.ledger_seq WHERE contract_create_year = #{calculateYear} AND contract_create_month = #{calculateMonth} AND contract_user_id = #{calculateUserId}
AND l.ledger_financial_company_cd = #{ledgerFinancialCompanyCd}
AND l.ledger_financial_branch_cd = #{ledgerFinancialBranchCd}
AND l.ledger_financial_product_cd = #{ledgerFinancialProductCd}
AND l.ledger_dealer_brand_cd = #{ledgerDealerBrandCd}
AND l.ledger_dealer_company_cd = #{ledgerDealerCompanyCd}
AND (l.ledger_car_name like CONCAT('%',#{searchText},'%') OR l.ledger_car_number like CONCAT('%',#{searchText},'%') OR l.ledger_other like CONCAT('%',#{searchText},'%') OR l.ledger_customer_name like CONCAT('%',#{searchText},'%') )
ORDER BY c.contract_create_date desc LIMIT #{pagePerRows} offset #{page}

  		SELECT count(*)
		FROM contract c
  		LEFT JOIN ledger l
  		ON c.contract_ledger_seq=l.ledger_seq
  		WHERE contract_create_year = #{calculateYear} 
  			AND contract_create_month = #{calculateMonth}
  			AND contract_user_id = #{calculateUserId}

				AND l.ledger_financial_company_cd = #{ledgerFinancialCompanyCd}

					AND l.ledger_financial_branch_cd = #{ledgerFinancialBranchCd}



				AND l.ledger_financial_product_cd = #{ledgerFinancialProductCd}


				AND l.ledger_dealer_brand_cd = #{ledgerDealerBrandCd}

					AND l.ledger_dealer_company_cd = #{ledgerDealerCompanyCd}



				AND (l.ledger_car_name like CONCAT('%',#{searchText},'%')
					OR l.ledger_car_number like CONCAT('%',#{searchText},'%')
					OR l.ledger_other like CONCAT('%',#{searchText},'%')
					OR l.ledger_customer_name like CONCAT('%',#{searchText},'%')
					)

  		SELECT *
  		,calculate_sliding_sum + calculate_add_fee_sum as calculate_promotion
		FROM calculate
  		WHERE calculate_year = #{calculateYear} 
  			AND calculate_month = #{calculateMonth}
  			AND calculate_user_id = #{calculateUserId}

select contract_user_id ,sum(contract_nomal_ag_fee_sum) as calculate_ag_fee_sum ,sum(contract_ag_sliding_sum) as calculate_sliding_sum ,sum(contract_add_ag_fee_sum) as calculate_add_fee_sum ,(sum(contract_nomal_ag_fee_sum)+sum(contract_ag_sliding_sum)+sum(contract_add_ag_fee_sum)) /1.1 as calculate_supply_sum ,(sum(contract_nomal_ag_fee_sum)+sum(contract_ag_sliding_sum)+sum(contract_add_ag_fee_sum)) - ((sum(contract_nomal_ag_fee_sum)+sum(contract_ag_sliding_sum)+sum(contract_add_ag_fee_sum)) /1.1)as calculate_surtax_sum ,(sum(contract_nomal_ag_fee_sum)+sum(contract_ag_sliding_sum)+sum(contract_add_ag_fee_sum))/1.1*0.967 as calculate_personal_ag_sum ,sum(contract_nomal_ag_fee_sum)+sum(contract_ag_sliding_sum)+sum(contract_add_ag_fee_sum) as calculate_business_sum ,sum(contract_nomal_ag_fee_sum)+sum(contract_ag_sliding_sum)+sum(contract_add_ag_fee_sum) as calculate_total_sum ,sum(contract_ag_sliding_sum) + sum(contract_add_ag_fee_sum) as calculate_promotion from contract con join calculate cal on con.contract_calculate_seq=cal.calculate_seq join ledger l on con.contract_ledger_seq=l.ledger_seq where calculate_year = #{calculateYear} AND calculate_month = #{calculateMonth} AND calculate_user_id = #{calculateUserId}
AND l.ledger_financial_company_cd = #{ledgerFinancialCompanyCd}
AND l.ledger_financial_branch_cd = #{ledgerFinancialBranchCd}
AND l.ledger_financial_product_cd = #{ledgerFinancialProductCd}
AND l.ledger_dealer_brand_cd = #{ledgerDealerBrandCd}
AND l.ledger_dealer_company_cd = #{ledgerDealerCompanyCd}
AND (l.ledger_car_name like CONCAT('%',#{searchText},'%') OR l.ledger_car_number like CONCAT('%',#{searchText},'%') OR l.ledger_other like CONCAT('%',#{searchText},'%') OR l.ledger_customer_name like CONCAT('%',#{searchText}, 0'%') )
group by con.contract_user_id

select calculate_seq ,user_business_type_cd ,(SELECT code_name FROM code WHERE code_id = u.user_business_type_cd) as user_business_type_cd_name ,user_name ,calculate_ag_fee_sum ,calculate_sliding_sum ,calculate_add_fee_sum ,calculate_ag_fee_sum+calculate_sliding_sum+calculate_add_fee_sum as calculatePersonalTotalPaySum ,(calculate_ag_fee_sum+calculate_sliding_sum+calculate_add_fee_sum)/1.1 as calculatePersonalTotalPaySupply ,(calculate_ag_fee_sum+calculate_sliding_sum+calculate_add_fee_sum)/1.1*0.967 as calculateBusinessIncomePay ,(calculate_ag_fee_sum+calculate_sliding_sum+calculate_add_fee_sum)/1.1-(calculate_ag_fee_sum+calculate_sliding_sum+calculate_add_fee_sum)/1.1*0.967 as calculateBusinessIncomePayReport ,calculate_supply_sum ,calculate_surtax_sum ,calculate_personal_ag_sum ,calculate_business_sum ,calculate_total_sum from calculate cal join users u on cal.calculate_user_id=u.user_id where calculate_year = #{calculateYear} AND calculate_month = #{calculateMonth}
AND user_business_type_cd = #{userBusinessTypeCd}
ORDER BY cal.calculate_create_date desc LIMIT #{pagePerRows} offset #{page}

  		select 
			sum(calculateAgFeeTotalDeposit) as calculateAgFeeTotalDeposit
			, sum(calculateAgFeeTotalWithdraw) as calculateAgFeeTotalWithdraw
			, sum(calculateAgFeeTotalDeposit)-sum(calculateAgFeeTotalWithdraw) as calculateAgFeeTotalDifference
		from "ViewCalculateSumByAdmin"
		where calculate_year = #{calculateYear} 
  			AND calculate_month = #{calculateMonth}

 				AND user_business_type_cd = #{userBusinessTypeCd}
