mapper.select:
SELECT * FROM (SELECT i.inquiry_ledger_seq , (SELECT inquiry_create_date FROM inquiry WHERE inquiry_ledger_seq = i.inquiry_ledger_seq LIMIT 1) as inquiry_create_date , (SELECT inquiry_create_date FROM inquiry WHERE inquiry_ledger_seq = i.inquiry_ledger_seq ORDER BY inquiry_create_date DESC LIMIT 1) as inquiry_last_date , l.ledger_financial_company_cd , (SELECT code_name FROM code WHERE code_id = l.ledger_financial_company_cd) as ledger_financial_company_cd_name , l.ledger_financial_branch_cd , (SELECT code_name FROM code WHERE code_id = l.ledger_financial_branch_cd) as ledger_financial_branch_cd_name , l.ledger_financial_product_cd , (SELECT code_name FROM code WHERE code_id = l.ledger_financial_product_cd) as ledger_financial_product_cd_name , l.ledger_customer_name , l.ledger_car_price , l.ledger_acquisition_cost FROM inquiry i JOIN ledger l ON i.inquiry_ledger_seq = l.ledger_seq WHERE (i.inquiry_from_user_id = #{inquiryFromUserId} or i.inquiry_to_user_id = #{inquiryFromUserId})
AND (l.ledger_car_name like CONCAT('%',#{searchText},'%') OR l.ledger_customer_name like CONCAT('%',#{searchText},'%') OR i.inquiry_content like CONCAT('%',#{searchText},'%') )
GROUP BY i.inquiry_ledger_seq , l.ledger_financial_company_cd , l.ledger_financial_branch_cd , l.ledger_financial_product_cd , l.ledger_customer_name , l.ledger_car_price , l.ledger_acquisition_cost ) data ORDER BY data.inquiry_last_date desc LIMIT #{pagePerRows} offset #{page}

SELECT * FROM (SELECT i.inquiry_ledger_seq , (SELECT inquiry_create_date FROM inquiry WHERE inquiry_ledger_seq = i.inquiry_ledger_seq LIMIT 1) as inquiry_create_date , (SELECT inquiry_create_date FROM inquiry WHERE inquiry_ledger_seq = i.inquiry_ledger_seq ORDER BY inquiry_create_date DESC LIMIT 1) as inquiry_last_date , l.ledger_financial_company_cd , (SELECT code_name FROM code WHERE code_id = l.ledger_financial_company_cd) as ledger_financial_company_cd_name , l.ledger_financial_branch_cd , (SELECT code_name FROM code WHERE code_id = l.ledger_financial_branch_cd) as ledger_financial_branch_cd_name , l.ledger_financial_product_cd , (SELECT code_name FROM code WHERE code_id = l.ledger_financial_product_cd) as ledger_financial_product_cd_name , l.ledger_customer_name , l.ledger_car_price , l.ledger_acquisition_cost FROM inquiry i JOIN ledger l ON i.inquiry_ledger_seq = l.ledger_seq WHERE 1=1
AND (l.ledger_car_name like CONCAT('%',#{searchText},'%') OR l.ledger_customer_name like CONCAT('%',#{searchText},'%') OR i.inquiry_content like CONCAT('%',#{searchText},'%') )
GROUP BY i.inquiry_ledger_seq , l.ledger_financial_company_cd , l.ledger_financial_branch_cd , l.ledger_financial_product_cd , l.ledger_customer_name , l.ledger_car_price , l.ledger_acquisition_cost ) data ORDER BY data.inquiry_last_date desc LIMIT #{pagePerRows} offset #{page}

SELECT *
FROM inquiry
WHERE inquiry_from_user_id = #{inquiryFromUserId}
AND inquiry_ledger_seq = #{inquiryLedgerSeq}

mapper.update:
UPDATE inquiry
SET inquiry_content = #{inquiryContent}
WHERE inquiry_seq = #{inquirySeq}
