mapper.select:
SELECT COUNT(*)
FROM users
WHERE user_id = #{userId}

SELECT * FROM (SELECT * ,(SELECT code_name FROM code WHERE code_id = user_business_type_cd) as user_business_type_cd_name ,(SELECT code_name FROM code WHERE code_id = user_code_company_cd) as user_code_company_cd_name ,(SELECT code_name FROM code WHERE code_id = user_permission_cd) as user_permission_cd_name FROM users WHERE 1=1
AND user_name like CONCAT('%',#{userName},'%')
AND user_id like CONCAT('%',#{userId},'%')
AND user_ag_company like CONCAT('%',#{userAgCompany},'%')
AND user_business_type_cd = #{userBusinessTypeCd}
AND user_permission_cd BETWEEN 1200 AND 1299
AND user_permission_cd BETWEEN 1100 AND 1199
AND user_permission_cd = 0
AND user_permission_cd = #{userPermissionCd}
AND user_use_yn = #{userUseYn}
AND (user_id like CONCAT('%',#{searchText},'%') OR user_name like CONCAT('%',#{searchText},'%') OR user_ag_company like CONCAT('%',#{searchText},'%') OR user_email like CONCAT('%',#{searchText},'%'))
) data ORDER BY data.user_join_date desc LIMIT #{pagePerRows} offset #{page}

mapper.insert:
INSERT INTO users(
	user_id
	, user_pw
	, user_salt
	, user_name
	, user_email
	, user_business_type_cd
	, user_code_company_cd
	, user_ag_company
	, user_join_date
	, user_permission_cd
	, user_use_yn
) VALUES (
	#{userId}
	, #{userPw}
	, #{userSalt}
	, #{userName}
	, #{userEmail}
	, #{userBusinessTypeCd}
	, #{userCodeCompanyCd}
	, #{userAgCompany}
	, now()
	, #{userPermissionCd}
	, 'Y'
)

mapper.update:
UPDATE users
SET user_name = #{userName}
	, user_email = #{userEmail}
	, user_ag_company = #{userAgCompany}
	, user_permission_cd = #{userPermissionCd}
	, user_code_company_cd = #{userCodeCompanyCd}
	, user_business_type_cd = #{userBusinessTypeCd}

		, user_use_yn = #{userUseYn}

WHERE user_id = #{userId}

UPDATE users
SET user_use_yn = 'N'
WHERE user_id = #{userId}
